import { makeAutoObservable } from "mobx";

export default class UserStore {
    constructor(basketStore) {
        this._isAuth = false;
        this._user = {};
        this._role = "USER";
        this._basketStore = basketStore;
        makeAutoObservable(this);
    }

    setIsAuth(bool) {
        this._isAuth = bool;
        if (bool) {
            this._basketStore.loadFromLocalStorage();
        } else {
            this._basketStore.clearBasket();
        }
    }

    setUser(user) {
        this._user = user;
    }

    setRole(role) {
        this._role = role;
    }

    get isAuth() {
        return this._isAuth;
    }

    get user() {
        return this._user;
    }

    get role() {
        return this._role;
    }
}
