import React, { useContext } from 'react';
import { Card, Col, Image, Button } from "react-bootstrap";
import { Rating } from 'react-simple-star-rating';
import { useNavigate } from "react-router-dom";
import { DEVICE_ROUTE } from "../utils/constants";
import { Context } from "../index";

const DeviceItem = ({ device }) => {
    const navigate = useNavigate();
    const { basket } = useContext(Context);

    // Переход на страницу товара
    const goToDevice = () => navigate(`${DEVICE_ROUTE}/${device.id}`);

    // Остановка всплытия для кнопки
    const handleAddToBasket = (e) => {
        e.stopPropagation();
        basket.addToBasket(device);
    };

    return (
        <Col md={3} className="mt-3">
            <Card
                style={{
                    width: 200,
                    minHeight: 360,
                    cursor: 'pointer',
                    padding: 16,
                    borderRadius: 16,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
                }}
                border="light"
                className="h-100 d-flex flex-column justify-content-between"
                onClick={goToDevice}
                tabIndex={0}
                role="button"
                onKeyDown={e => { if (e.key === 'Enter') goToDevice(); }}
            >
                <div
                    style={{
                        width: 160,
                        height: 160,
                        margin: '0 auto',
                        background: '#f8f9fa',
                        borderRadius: 12,
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        overflow: 'hidden'
                    }}
                >
                    <Image
                        width={160}
                        height={160}
                        style={{
                            objectFit: 'contain',
                            borderRadius: 12,
                            background: '#fff'
                        }}
                        src={process.env.REACT_APP_API_URL + device.img}
                        alt={device.name}
                    />
                </div>
                <div style={{ marginTop: 14 }}>
                    <div
                        style={{
                            fontWeight: 600,
                            fontSize: 16,
                            marginBottom: 5,
                            textAlign: 'center',
                            color: '#000', // ЧЁРНЫЙ ЦВЕТ ДЛЯ НАЗВАНИЯ
                            cursor: 'pointer'
                        }}
                        onClick={goToDevice}
                        tabIndex={0}
                        role="button"
                        onKeyDown={e => { if (e.key === 'Enter') goToDevice(); }}
                    >
                        {device.name}
                    </div>
                    <div className="d-flex justify-content-center align-items-center mb-2">
                        <Rating
                            initialValue={device.rating || 0}
                            readonly
                            size={14}
                            allowFraction
                            fillColor="#ffbc0b"
                            emptyColor="#ccc"
                        />
                        <span style={{ fontSize: 13, color: '#888', marginLeft: 4 }}>
                            {device.rating?.toFixed(1) || '0.0'}
                        </span>
                    </div>
                    <div className="d-flex justify-content-between align-items-center mt-3">
                        <span
                            style={{
                                fontWeight: 700,
                                fontSize: 19,
                                color: '#1976d2' // Синий цвет для цены
                            }}
                        >
                            {device.price} ₽
                        </span>
                        <Button
                            variant="primary"
                            style={{
                                borderRadius: 22,
                                padding: '7px 18px',
                                fontWeight: 600,
                                fontSize: 15,
                                boxShadow: '0 2px 4px rgba(0,123,255,0.07)'
                            }}
                            onClick={handleAddToBasket}
                        >
                            В корзину
                        </Button>
                    </div>
                </div>
            </Card>
        </Col>
    );
};

export default DeviceItem;
