import React, { useRef, useEffect } from "react";
import "./MultiRangeSlider.css";

const MultiRangeSlider = ({
                              min,
                              max,
                              minValue,
                              maxValue,
                              onChange
                          }) => {
    const minValRef = useRef(null);
    const maxValRef = useRef(null);
    const range = useRef(null);

    // Вычисляем проценты для трека
    const getPercent = value => Math.round(((value - min) / (max - min)) * 100);

    useEffect(() => {
        if (maxValRef.current) {
            const minPercent = getPercent(minValue);
            const maxPercent = getPercent(maxValue);

            if (range.current) {
                range.current.style.left = `${minPercent}%`;
                range.current.style.width = `${maxPercent - minPercent}%`;
            }
        }
    }, [minValue, maxValue, min, max]);

    return (
        <div className="slider-container">
            <input
                type="range"
                min={min}
                max={max}
                value={minValue}
                ref={minValRef}
                onChange={e => {
                    const val = Math.min(Number(e.target.value), maxValue - 1);
                    onChange({ min: val, max: maxValue });
                }}
                className="thumb thumb--left"
                style={{ zIndex: minValue > max - 100 ? "5" : undefined }}
            />
            <input
                type="range"
                min={min}
                max={max}
                value={maxValue}
                ref={maxValRef}
                onChange={e => {
                    const val = Math.max(Number(e.target.value), minValue + 1);
                    onChange({ min: minValue, max: val });
                }}
                className="thumb thumb--right"
            />

            <div className="slider">
                <div className="slider__track" />
                <div ref={range} className="slider__range" />
                <div className="slider__left-value">{minValue}</div>
                <div className="slider__right-value">{maxValue}</div>
            </div>
        </div>
    );
};

export default MultiRangeSlider;
