import React, { useContext, useEffect, useState, useMemo } from 'react';
import { Col, Container, Row, Form, Button } from "react-bootstrap";
import TypeBar from '../components/TypeBar';
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchDevices } from "../http/deviceAPI";
import { observer } from "mobx-react-lite";
import Pages from "../components/Pages";
import DualRangeSlider from "../components/DualRangeSlider"; // –ù–æ–≤—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç

const Shop = observer(() => {
    const { device } = useContext(Context);
    const [search, setSearch] = useState("");
    const [isLoading, setIsLoading] = useState(true);

    // –§–∏–∫—Å–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –≥—Ä–∞–Ω–∏—Ü—ã –¥–ª—è —Å–ª–∞–π–¥–µ—Ä–∞
    const ABS_MIN = 0;
    const ABS_MAX = 150000;

    // –î–∏–∞–ø–∞–∑–æ–Ω —Ü–µ–Ω —Ç–æ–≤–∞—Ä–æ–≤ (–¥–ª—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏)
    const prices = device.devices.map(dev => dev.price);
    const minPrice = prices.length ? Math.min(...prices) : ABS_MIN;
    const maxPrice = prices.length ? Math.max(...prices) : ABS_MAX;

    // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –¥–∏–∞–ø–∞–∑–æ–Ω–∞ —Ü–µ–Ω [min, max]
    const [priceRange, setPriceRange] = useState([ABS_MIN, ABS_MAX]);

    useEffect(() => {
        setIsLoading(true);
        Promise.all([
            fetchTypes(),
            fetchBrands(),
            fetchDevices(null, null, 1, 100)
        ])
            .then(([typesData, brandsData, devicesData]) => {
                device.setTypes(typesData);
                device.setBrands(brandsData);
                device.setDevices(devicesData.rows);
                device.setTotalCount(devicesData.count);
            })
            .finally(() => setIsLoading(false));
    }, [device]);

    useEffect(() => {
        setIsLoading(true);
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 3)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .finally(() => setIsLoading(false));
    }, [device.page, device.selectedType, device.selectedBrand]);

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è —É—Å—Ç—Ä–æ–π—Å—Ç–≤
    const filteredDevices = useMemo(() => {
        return device.devices.filter(dev =>
            dev.name.toLowerCase().includes(search.toLowerCase()) &&
            dev.price >= priceRange[0] &&
            dev.price <= priceRange[1]
        );
    }, [device.devices, search, priceRange]);

    // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –¥–ª—è –∏–Ω–ø—É—Ç–æ–≤
    const handleMinInputChange = (e) => {
        const value = Math.max(ABS_MIN, Math.min(Number(e.target.value), priceRange[1]));
        setPriceRange([value, priceRange[1]]);
    };

    const handleMaxInputChange = (e) => {
        const value = Math.min(ABS_MAX, Math.max(Number(e.target.value), priceRange[0]));
        setPriceRange([priceRange[0], value]);
    };

    // –°–±—Ä–æ—Å —Ñ–∏–ª—å—Ç—Ä–∞
    const handleResetPrice = () => {
        setPriceRange([ABS_MIN, ABS_MAX]);
    };

    return (
        <Container>
            <Row className="mt-2 align-items-center">
                <Col md={3}>
                    <Form.Control
                        type="text"
                        placeholder="üîç –ü–æ–∏—Å–∫ –ø–æ –∫–∞—Ç–∞–ª–æ–≥—É..."
                        value={search}
                        onChange={e => setSearch(e.target.value)}
                        style={{
                            marginBottom: 0,
                            borderRadius: 12,
                            fontSize: 17,
                            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                            padding: "10px 16px"
                        }}
                    />
                </Col>
                <Col md={9}>
                    <BrandBar />
                </Col>
            </Row>
            <Row className="mt-3">
                <Col md={3}>
                    <TypeBar />
                    {/* –§–∏–ª—å—Ç—Ä –ø–æ —Ü–µ–Ω–µ */}
                    <div className="mt-4 p-3" style={{
                        background: '#f8f9fa',
                        borderRadius: 12,
                        boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
                    }}>
                        <h6 className="mb-3">–¶–µ–Ω–∞</h6>
                        <DualRangeSlider
                            min={ABS_MIN}
                            max={ABS_MAX}
                            values={priceRange}
                            onChange={(newRange) => setPriceRange(newRange)}
                        />
                        <div className="d-flex justify-content-between mt-2">
                            <Form.Control
                                type="number"
                                value={priceRange[0]}
                                onChange={handleMinInputChange}
                                style={{ width: '45%' }}
                                placeholder="–ú–∏–Ω. —Ü–µ–Ω–∞"
                            />
                            <span className="mx-2">‚Äî</span>
                            <Form.Control
                                type="number"
                                value={priceRange[1]}
                                onChange={handleMaxInputChange}
                                style={{ width: '45%' }}
                                placeholder="–ú–∞–∫—Å. —Ü–µ–Ω–∞"
                            />
                        </div>
                        {/* –ü–æ–∫–∞–∑–∞—Ç—å –¥–∏–∞–ø–∞–∑–æ–Ω —Ç–æ–≤–∞—Ä–æ–≤ –¥–ª—è —Å–ø—Ä–∞–≤–∫–∏ */}
                        <div className="text-muted mt-2" style={{ fontSize: '12px' }}>
                            –¢–æ–≤–∞—Ä—ã –æ—Ç {minPrice.toLocaleString()} –¥–æ {maxPrice.toLocaleString()} ‚ÇΩ
                        </div>
                        <Button
                            variant="link"
                            className="mt-2 p-0 w-100"
                            onClick={handleResetPrice}
                        >
                            –°–±—Ä–æ—Å–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä
                        </Button>
                    </div>
                </Col>
                <Col md={9}>
                    {isLoading ? (
                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">–ó–∞–≥—Ä—É–∑–∫–∞...</span>
                            </div>
                        </div>
                    ) : (
                        <>
                            <DeviceList devices={filteredDevices} />
                            <Pages />
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
