import React, { useContext, useState, useEffect } from "react";
import { BrowserRouter } from "react-router-dom";
import AppRouter from "./components/AppRouter";
import NavBar from "./components/NavBar";
import { observer } from "mobx-react";
import { Context } from "./index";
import Spinner from "react-bootstrap/Spinner";
import { check } from "./http/userAPI"; // Импортируйте функцию проверки токена

const App = observer(() => {
    const { user } = useContext(Context);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Проверка токена пользователя при запуске приложения
        check()
            .then(data => {
                user.setUser(data);      // Сохраняем данные пользователя из токена
                user.setIsAuth(true);    // Отмечаем пользователя как авторизованного
            })
            .catch(() => {
                user.setIsAuth(false);   // Если токен невалиден — пользователь не авторизован
            })
            .finally(() => setLoading(false)); // Отключаем спиннер после проверки
    }, [user]);

    if (loading) {
        return (
            <div className="d-flex justify-content-center align-items-center" style={{height: '100vh'}}>
                <Spinner animation="grow" variant="primary" />
            </div>
        );
    }

    return (
        <BrowserRouter>
            <NavBar />
            <AppRouter />
        </BrowserRouter>
    );
});

export default App;
