import React, { useContext } from 'react';
import { Navbar, Nav, Button, Container } from 'react-bootstrap';
import { NavLink, useNavigate } from 'react-router-dom';
import { SHOP_ROUTE, ADMIN_ROUTE, LOGIN_ROUTE } from '../utils/constants';
import { Context } from '../index';
import { observer } from "mobx-react-lite";

const NavBar = observer(() => {
    const { user } = useContext(Context);
    const navigate = useNavigate();

    const logOut = () => {
        user.setUser({});
        user.setIsAuth(false);
        localStorage.removeItem('token'); // Очищаем токен из localStorage
        navigate(LOGIN_ROUTE);
    };

    const handleAuthClick = () => {
        navigate(LOGIN_ROUTE); // Переход на страницу авторизации
    };

    return (
        <Navbar bg="dark" variant="dark" expand="lg">
            <Container fluid>
                <Navbar.Brand as={NavLink} to={SHOP_ROUTE} style={{ color: 'white' }}>
                    ElecShopic
                </Navbar.Brand>

                <Navbar.Toggle aria-controls="basic-navbar-nav" />

                <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
                    {user.isAuth ? (
                        <Nav className="ms-auto">
                            <Button
                                variant="outline-light"
                                onClick={() => navigate(ADMIN_ROUTE)}
                                className="me-2"
                            >
                                Админ панель
                            </Button>
                            <Button
                                variant="outline-light"
                                onClick={logOut}
                            >
                                Выйти
                            </Button>
                        </Nav>
                    ) : (
                        <Nav className="ms-auto">
                            <Button
                                variant="outline-light"
                                onClick={handleAuthClick}
                            >
                                Авторизация
                            </Button>
                        </Nav>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
});

export default NavBar;
