import React, { useState, useContext, useEffect } from 'react';
import { Container, Button, Row, Col, ListGroup } from 'react-bootstrap';
import CreateBrand from '../components/modals/CreateBrand';
import CreateType from '../components/modals/CreateType';
import CreateDevice from '../components/modals/CreateDevice';
import DeleteConfirm from '../components/modals/DeleteConfirm';
import { Context } from '../index';
import { fetchTypes, fetchBrands, deleteType, deleteBrand } from '../http/deviceAPI';
import { observer } from 'mobx-react-lite';

const Admin = observer(() => {
    const { device } = useContext(Context);
    const [brandVisible, setBrandVisible] = useState(false);
    const [typeVisible, setTypeVisible] = useState(false);
    const [deviceVisible, setDeviceVisible] = useState(false);
    const [deleteVisible, setDeleteVisible] = useState(false);
    const [deleteItem, setDeleteItem] = useState(null);
    const [deleteLoading, setDeleteLoading] = useState(false);

    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data));
        fetchBrands().then(data => device.setBrands(data));
    }, [device]);

    const handleDelete = (item, type) => {
        setDeleteItem({ ...item, type });
        setDeleteVisible(true);
    };

    const confirmDelete = async () => {
        if (!deleteItem) return;

        setDeleteLoading(true);
        try {
            if (deleteItem.type === 'type') {
                await deleteType(deleteItem.id);
                const types = await fetchTypes();
                device.setTypes(types);
                if (device.selectedType.id === deleteItem.id) {
                    device.setSelectedType({});
                }
            } else if (deleteItem.type === 'brand') {
                await deleteBrand(deleteItem.id);
                const brands = await fetchBrands();
                device.setBrands(brands);
                if (device.selectedBrand.id === deleteItem.id) {
                    device.setSelectedBrand({});
                }
            }
            setDeleteVisible(false);
            setDeleteItem(null);
        } catch (error) {
            console.error('Ошибка при удалении:', error);
            alert('Ошибка при удалении');
        } finally {
            setDeleteLoading(false);
        }
    };

    return (
        <Container className="d-flex flex-column">
            <Row className="mt-4">
                <Col md={4}>
                    <Button
                        variant="outline-dark"
                        className="mt-2 p-2"
                        onClick={() => setTypeVisible(true)}
                        style={{ width: '100%' }}
                    >
                        Добавить тип
                    </Button>
                    <ListGroup className="mt-3">
                        {device.types.map(type =>
                            <ListGroup.Item
                                key={type.id}
                                className="d-flex justify-content-between align-items-center"
                            >
                                {type.name}
                                <Button
                                    variant="outline-danger"
                                    size="sm"
                                    onClick={() => handleDelete(type, 'type')}
                                >
                                    ✕
                                </Button>
                            </ListGroup.Item>
                        )}
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Button
                        variant="outline-dark"
                        className="mt-2 p-2"
                        onClick={() => setBrandVisible(true)}
                        style={{ width: '100%' }}
                    >
                        Добавить бренд
                    </Button>
                    <ListGroup className="mt-3">
                        {device.brands.map(brand =>
                            <ListGroup.Item
                                key={brand.id}
                                className="d-flex justify-content-between align-items-center"
                            >
                                {brand.name}
                                <Button
                                    variant="outline-danger"
                                    size="sm"
                                    onClick={() => handleDelete(brand, 'brand')}
                                >
                                    ✕
                                </Button>
                            </ListGroup.Item>
                        )}
                    </ListGroup>
                </Col>

                <Col md={4}>
                    <Button
                        variant="outline-dark"
                        className="mt-2 p-2"
                        onClick={() => setDeviceVisible(true)}
                        style={{ width: '100%' }}
                    >
                        Добавить устройство
                    </Button>
                </Col>
            </Row>

            <CreateType show={typeVisible} onHide={() => setTypeVisible(false)} />
            <CreateBrand show={brandVisible} onHide={() => setBrandVisible(false)} />
            <CreateDevice show={deviceVisible} onHide={() => setDeviceVisible(false)} />

            <DeleteConfirm
                show={deleteVisible}
                onHide={() => setDeleteVisible(false)}
                onConfirm={confirmDelete}
                title="Подтверждение удаления"
                message={`Вы уверены, что хотите удалить ${deleteItem?.name}?`}
                loading={deleteLoading}
            />
        </Container>
    );
});

export default Admin;
