import React, { useState, useEffect, useMemo, useContext } from "react";
import DualRangeSlider from "../components/DualRangeSlider";
import "./DualRangeSlider.css";
// ...остальные импорты

const ABS_MIN = 0;
const ABS_MAX = 150000;

const Shop = observer(() => {
    // ...твой код
    const [priceRange, setPriceRange] = useState([ABS_MIN, ABS_MAX]);
    const [inputMin, setInputMin] = useState(ABS_MIN);
    const [inputMax, setInputMax] = useState(ABS_MAX);

    // Синхронизируем input-ы и слайдер
    useEffect(() => {
        setInputMin(priceRange[0]);
        setInputMax(priceRange[1]);
    }, [priceRange[0], priceRange[1]]);

    // Фильтрация
    const filteredDevices = useMemo(() =>
        device.devices.filter(dev =>
            dev.name.toLowerCase().includes(search.toLowerCase()) &&
            dev.price >= priceRange[0] &&
            dev.price <= priceRange[1]
        ), [device.devices, search, priceRange]
    );

    return (
        // ...
        <div className="mt-4 p-3" style={{
            background: '#f8f9fa',
            borderRadius: 12,
            boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
        }}>
            <h6 className="mb-3">Цена</h6>
            <DualRangeSlider
                min={ABS_MIN}
                max={ABS_MAX}
                minValue={inputMin}
                maxValue={inputMax}
                onChange={([min, max]) => {
                    setInputMin(min);
                    setInputMax(max);
                    setPriceRange([min, max]);
                }}
            />
            <div className="d-flex justify-content-between mt-2">
                <Form.Control
                    type="number"
                    value={inputMin}
                    min={ABS_MIN}
                    max={inputMax - 1}
                    onChange={e => {
                        const val = Math.min(Number(e.target.value), inputMax - 1);
                        setInputMin(val);
                        setPriceRange([val, inputMax]);
                    }}
                    style={{ width: '45%' }}
                />
                <span className="mx-2">—</span>
                <Form.Control
                    type="number"
                    value={inputMax}
                    min={inputMin + 1}
                    max={ABS_MAX}
                    onChange={e => {
                        const val = Math.max(Number(e.target.value), inputMin + 1);
                        setInputMax(val);
                        setPriceRange([inputMin, val]);
                    }}
                    style={{ width: '45%' }}
                />
            </div>
        </div>
        // ...
    );
});
