import React, { useContext, useEffect, useState, useMemo } from 'react';
import { Col, Container, Row, Form, Button } from "react-bootstrap";
import TypeBar from '../components/TypeBar';
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchDevices } from "../http/deviceAPI";
import { observer } from "mobx-react-lite";
import Pages from "../components/Pages";
import MultiRangeSlider from "../components/MultiRangeSlider";

const Shop = observer(() => {
    const { device } = useContext(Context);
    const [search, setSearch] = useState("");
    const [isLoading, setIsLoading] = useState(true);

    // –î–∏–∞–ø–∞–∑–æ–Ω —Ü–µ–Ω –≤—Å–µ—Ö —Ç–æ–≤–∞—Ä–æ–≤
    const prices = device.devices.map(dev => dev.price);
    const minPrice = prices.length ? Math.min(...prices) : 0;
    const maxPrice = prices.length ? Math.max(...prices) : 1000;

    // "–ê–∫—Ç–∏–≤–Ω—ã–π" —Ñ–∏–ª—å—Ç—Ä (–ø—Ä–∏–º–µ–Ω—ë–Ω–Ω—ã–π)
    const [priceRange, setPriceRange] = useState({ min: minPrice, max: maxPrice });

    // "–°—ã—Ä—ã–µ" –∑–Ω–∞—á–µ–Ω–∏—è, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–∞–±–∏—Ä–∞–µ—Ç
    const [inputMin, setInputMin] = useState(minPrice);
    const [inputMax, setInputMax] = useState(maxPrice);

    // –°–±—Ä–æ—Å–∏—Ç—å –∏–Ω–ø—É—Ç—ã –ø—Ä–∏ —Å–º–µ–Ω–µ –∞—Å—Å–æ—Ä—Ç–∏–º–µ–Ω—Ç–∞
    useEffect(() => {
        setPriceRange({ min: minPrice, max: maxPrice });
        setInputMin(minPrice);
        setInputMax(maxPrice);
    }, [minPrice, maxPrice]);

    useEffect(() => {
        setIsLoading(true);
        Promise.all([
            fetchTypes(),
            fetchBrands(),
            fetchDevices(null, null, 1, 100)
        ])
            .then(([typesData, brandsData, devicesData]) => {
                device.setTypes(typesData);
                device.setBrands(brandsData);
                device.setDevices(devicesData.rows);
                device.setTotalCount(devicesData.count);
            })
            .finally(() => setIsLoading(false));
    }, [device]);

    useEffect(() => {
        setIsLoading(true);
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 3)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .finally(() => setIsLoading(false));
    }, [device.page, device.selectedType, device.selectedBrand]);

    // –ü—Ä–∏–º–µ–Ω–µ–Ω–∏–µ —Ñ–∏–ª—å—Ç—Ä–∞ (–ø–æ –∫–Ω–æ–ø–∫–µ)
    const handleApplyPrice = () => {
        const min = Math.max(minPrice, Math.min(Number(inputMin), inputMax - 1));
        const max = Math.min(maxPrice, Math.max(Number(inputMax), min + 1));
        setPriceRange({ min, max });
        setInputMin(min);
        setInputMax(max);
    };

    // –°–±—Ä–æ—Å —Ñ–∏–ª—å—Ç—Ä–∞
    const handleResetPrice = () => {
        setPriceRange({ min: minPrice, max: maxPrice });
        setInputMin(minPrice);
        setInputMax(maxPrice);
    };

    // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è —É—Å—Ç—Ä–æ–π—Å—Ç–≤ –ø–æ –ø–æ–∏—Å–∫—É –∏ —Ü–µ–Ω–µ (–ø—Ä–∏–º–µ–Ω—ë–Ω–Ω—ã–π –¥–∏–∞–ø–∞–∑–æ–Ω)
    const filteredDevices = useMemo(() => {
        return device.devices.filter(dev =>
            dev.name.toLowerCase().includes(search.toLowerCase()) &&
            dev.price >= priceRange.min &&
            dev.price <= priceRange.max
        );
    }, [device.devices, search, priceRange]);

    return (
        <Container>
            <Row className="mt-2 align-items-center">
                <Col md={3}>
                    <Form.Control
                        type="text"
                        placeholder="üîç –ü–æ–∏—Å–∫ –ø–æ –∫–∞—Ç–∞–ª–æ–≥—É..."
                        value={search}
                        onChange={e => setSearch(e.target.value)}
                        style={{
                            marginBottom: 0,
                            borderRadius: 12,
                            fontSize: 17,
                            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                            padding: "10px 16px"
                        }}
                    />
                </Col>
                <Col md={9}>
                    <BrandBar />
                </Col>
            </Row>
            <Row className="mt-3">
                <Col md={3}>
                    <TypeBar />
                    {/* –§–∏–ª—å—Ç—Ä –ø–æ —Ü–µ–Ω–µ */}
                    <div className="mt-4 p-3" style={{
                        background: '#f8f9fa',
                        borderRadius: 12,
                        boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
                    }}>
                        <h6 className="mb-3">–¶–µ–Ω–∞</h6>
                        <MultiRangeSlider
                            min={minPrice}
                            max={maxPrice}
                            minValue={inputMin}
                            maxValue={inputMax}
                            onChange={({ min, max }) => {
                                setInputMin(min);
                                setInputMax(max);
                            }}
                        />
                        <div className="d-flex justify-content-between mt-2">
                            <Form.Control
                                type="number"
                                value={inputMin}
                                min={minPrice}
                                max={inputMax - 1}
                                onChange={e => setInputMin(e.target.value)}
                                style={{ width: '45%' }}
                            />
                            <span className="mx-2">‚Äî</span>
                            <Form.Control
                                type="number"
                                value={inputMax}
                                min={Number(inputMin) + 1}
                                max={maxPrice}
                                onChange={e => setInputMax(e.target.value)}
                                style={{ width: '45%' }}
                            />
                        </div>
                        <Button
                            variant="primary"
                            className="mt-2 w-100"
                            onClick={handleApplyPrice}
                        >
                            –ü—Ä–∏–º–µ–Ω–∏—Ç—å
                        </Button>
                        <Button
                            variant="link"
                            className="mt-1 p-0 w-100"
                            onClick={handleResetPrice}
                        >
                            –°–±—Ä–æ—Å–∏—Ç—å —Ñ–∏–ª—å—Ç—Ä
                        </Button>
                    </div>
                </Col>
                <Col md={9}>
                    {isLoading ? (
                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">–ó–∞–≥—Ä—É–∑–∫–∞...</span>
                            </div>
                        </div>
                    ) : (
                        <>
                            <DeviceList devices={filteredDevices} />
                            <Pages />
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
