import React, { useContext } from 'react';
import { Card, Col, Image, Button } from "react-bootstrap";
import { Rating } from 'react-simple-star-rating';
import { useNavigate } from "react-router-dom";
import { DEVICE_ROUTE } from "../utils/constants";
import { Context } from "../index";

const DeviceItem = ({ device }) => {
    const navigate = useNavigate();
    const { basket } = useContext(Context);

    return (
        <Col md={3} className="mt-3">
            <Card
                style={{
                    width: 180,
                    minHeight: 340,
                    cursor: 'pointer',
                    padding: 16,
                    borderRadius: 16,
                    boxShadow: '0 2px 8px rgba(0,0,0,0.05)'
                }}
                border="light"
                className="h-100 d-flex flex-column justify-content-between"
            >
                <Image
                    width={150}
                    height={150}
                    style={{ objectFit: 'cover', borderRadius: 12, margin: '0 auto' }}
                    src={process.env.REACT_APP_API_URL + device.img}
                    onClick={() => navigate(`${DEVICE_ROUTE}/${device.id}`)}
                />
                <div style={{ marginTop: 12 }}>
                    <div style={{
                        fontWeight: 600,
                        fontSize: 16,
                        marginBottom: 4,
                        textAlign: 'center'
                    }}>
                        {device.name}
                    </div>
                    <div className="d-flex justify-content-center align-items-center mb-2">
                        <Rating
                            initialValue={device.rating || 0}
                            readonly
                            size={14}
                            allowFraction
                            fillColor="#ffbc0b"
                            emptyColor="#ccc"
                        />
                        <span style={{ fontSize: 13, color: '#888', marginLeft: 4 }}>
                            {device.rating?.toFixed(1) || '0.0'}
                        </span>
                    </div>
                    <div className="d-flex justify-content-between align-items-center" style={{ marginTop: 10 }}>
                        <span style={{
                            fontWeight: 700,
                            fontSize: 18,
                            color: '#222'
                        }}>
                            {device.price} ₽
                        </span>
                        <Button
                            variant="primary"
                            style={{
                                borderRadius: 24,
                                padding: '6px 18px',
                                fontWeight: 600,
                                fontSize: 15,
                                boxShadow: '0 2px 4px rgba(0,123,255,0.07)'
                            }}
                            onClick={(e) => {
                                e.stopPropagation();
                                basket.addToBasket(device);
                            }}
                        >
                            В корзину
                        </Button>
                    </div>
                </div>
            </Card>
        </Col>
    );
};

export default DeviceItem;
