import React, { useState, useEffect, useContext } from 'react';
import { Rating } from 'react-simple-star-rating';
import { setRating, fetchAverageRating } from '../http/ratingAPI';
import { Context } from '../index';

const DeviceRating = ({ deviceId, onAvgChange }) => {
    const { user } = useContext(Context);
    const [rating, setRatingState] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const loadRating = async () => {
            try {
                setLoading(true);
                const avg = await fetchAverageRating(deviceId);
                setRatingState(avg * 20);
                if (onAvgChange) onAvgChange(avg);
            } catch (e) {
                setError('Ошибка загрузки рейтинга');
            } finally {
                setLoading(false);
            }
        };
        loadRating();
    }, [deviceId, onAvgChange]);

    const handleRate = async (rate) => {
        if (!user.isAuth) return;
        setLoading(true);
        setError(null);

        // rate: 0-100 → 1-5 (целое)
        const stars = Math.round(rate / 20);
        console.log('Выставлено звёзд:', stars);

        try {
            setRatingState(stars * 20);
            await setRating(deviceId, stars);
            const avg = await fetchAverageRating(deviceId);
            setRatingState(avg * 20);
            if (onAvgChange) onAvgChange(avg);
        } catch (e) {
            setError('Ошибка сохранения оценки');
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="d-flex justify-content-center">
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Загрузка...</span>
                </div>
            </div>
        );
    }

    return (
        <div className="mb-3">
            <Rating
                onClick={handleRate}
                initialValue={rating}
                size={30}
                allowFraction={false}
                transition
                readonly={!user.isAuth || loading}
            />
            {error && (
                <div className="text-danger mt-2">
                    <small>{error}</small>
                </div>
            )}
        </div>
    );
};

export default DeviceRating;
