import React, { useContext, useEffect, useState, useMemo } from 'react';
import {Col, Container, Row, Form, Button} from "react-bootstrap";
import TypeBar from '../components/TypeBar';
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchDevices } from "../http/deviceAPI";
import { observer } from "mobx-react-lite";
import Pages from "../components/Pages";
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

const Shop = observer(() => {
    const { device } = useContext(Context);
    const [search, setSearch] = useState("");
    const [isLoading, setIsLoading] = useState(true);

    // Состояния для фильтра по цене
    const [priceRange, setPriceRange] = useState([0, 100000]);
    const [activePriceFilter, setActivePriceFilter] = useState(false);

    useEffect(() => {
        setIsLoading(true);
        Promise.all([
            fetchTypes(),
            fetchBrands(),
            fetchDevices(null, null, 1, 100) // Увеличиваем лимит для корректного расчёта цен
        ])
            .then(([typesData, brandsData, devicesData]) => {
                device.setTypes(typesData);
                device.setBrands(brandsData);
                device.setDevices(devicesData.rows);
                device.setTotalCount(devicesData.count);

                // Рассчитываем реальный диапазон цен
                if (devicesData.rows.length > 0) {
                    const prices = devicesData.rows.map(d => d.price);
                    const minPrice = Math.min(...prices);
                    const maxPrice = Math.max(...prices);
                    setPriceRange([minPrice, maxPrice]);
                }
            })
            .finally(() => setIsLoading(false));
    }, [device]);

    useEffect(() => {
        setIsLoading(true);
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 3)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .finally(() => setIsLoading(false));
    }, [device.page, device.selectedType, device.selectedBrand]);

    // Фильтрация устройств
    const filteredDevices = useMemo(() => {
        return device.devices.filter(dev => {
            const matchesSearch = dev.name.toLowerCase().includes(search.toLowerCase());
            const matchesPrice = !activePriceFilter ||
                (dev.price >= priceRange[0] && dev.price <= priceRange[1]);
            return matchesSearch && matchesPrice;
        });
    }, [device.devices, search, priceRange, activePriceFilter]);

    // Обработчики для слайдера
    const handleSliderChange = (value) => {
        setPriceRange(value);
        setActivePriceFilter(true);
    };

    const handleMinInputChange = (e) => {
        const value = Math.max(0, Number(e.target.value));
        setPriceRange([value, priceRange[1]]);
        setActivePriceFilter(true);
    };

    const handleMaxInputChange = (e) => {
        const value = Math.max(priceRange[0], Number(e.target.value));
        setPriceRange([priceRange[0], value]);
        setActivePriceFilter(true);
    };

    return (
        <Container>
            {/* Первая строка: поиск и бренды */}
            <Row className="mt-2 align-items-center">
                <Col md={3}>
                    <Form.Control
                        type="text"
                        placeholder="🔍 Поиск по каталогу..."
                        value={search}
                        onChange={e => setSearch(e.target.value)}
                        style={{
                            marginBottom: 0,
                            borderRadius: 12,
                            fontSize: 17,
                            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                            padding: "10px 16px"
                        }}
                    />
                </Col>
                <Col md={9}>
                    <BrandBar />
                </Col>
            </Row>

            {/* Вторая строка: TypeBar и карточки */}
            <Row className="mt-3">
                <Col md={3}>
                    <TypeBar />

                    {/* Фильтр по цене */}
                    <div className="mt-4 p-3" style={{
                        background: '#f8f9fa',
                        borderRadius: 12,
                        boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
                    }}>
                        <h6 className="mb-3">Фильтр по цене</h6>

                        {/* Слайдер */}
                        <Slider
                            range
                            min={0}
                            max={100000}
                            step={100}
                            value={priceRange}
                            onChange={handleSliderChange}
                            trackStyle={{ backgroundColor: '#1976d2', height: 4 }}
                            handleStyle={{
                                borderColor: '#1976d2',
                                height: 18,
                                width: 18,
                                marginTop: -7
                            }}
                        />

                        {/* Поля ввода */}
                        <div className="d-flex justify-content-between mt-3">
                            <Form.Control
                                type="number"
                                value={priceRange[0]}
                                onChange={handleMinInputChange}
                                style={{ width: '45%' }}
                            />
                            <span className="mx-2">—</span>
                            <Form.Control
                                type="number"
                                value={priceRange[1]}
                                onChange={handleMaxInputChange}
                                style={{ width: '45%' }}
                            />
                        </div>

                        {/* Кнопка сброса */}
                        {activePriceFilter && (
                            <Button
                                variant="link"
                                className="mt-2 p-0"
                                onClick={() => setActivePriceFilter(false)}
                            >
                                Сбросить фильтр
                            </Button>
                        )}
                    </div>
                </Col>

                <Col md={9}>
                    {isLoading ? (
                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">Загрузка...</span>
                            </div>
                        </div>
                    ) : (
                        <>
                            <DeviceList devices={filteredDevices} />
                            <Pages />
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
