import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { createBrand } from "../http/deviceAPI"; // Импорт функции для создания бренда

const CreateBrand = ({ show, onHide }) => {
    const [value, setValue] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const addBrand = async () => {
        if (!value.trim()) return;
        setLoading(true);
        setError(null);
        try {
            await createBrand({ name: value });
            setValue('');
            onHide();
        } catch (e) {
            setError(e.response?.data?.message || "Ошибка добавления бренда");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить бренд
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Control
                        value={value}
                        onChange={e => setValue(e.target.value)}
                        placeholder="Введите название бренда"
                        disabled={loading}
                        autoFocus
                    />
                    {error && <div className="text-danger mt-2">{error}</div>}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button variant="outline-danger" onClick={onHide} disabled={loading}>
                    Закрыть
                </Button>
                <Button
                    variant="outline-success"
                    onClick={addBrand}
                    disabled={loading || !value.trim()}
                >
                    {loading ? "Добавление..." : "Добавить"}
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateBrand;
