import React, { useState, useEffect, useContext } from 'react';
import { Rating } from 'react-simple-star-rating';
import { setRating, fetchAverageRating } from '../http/ratingAPI';
import { Context } from '../index';

const DeviceRating = ({ deviceId }) => {
    const { user } = useContext(Context);
    const [rating, setRatingState] = useState(0);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Загрузка среднего рейтинга
    useEffect(() => {
        const loadRating = async () => {
            try {
                setLoading(true);
                const avg = await fetchAverageRating(deviceId);
                setRatingState(avg * 20);
                if (onAvgChange) onAvgChange(avg); // Передаём среднее в родителя
            } catch (e) {
                setError('Ошибка загрузки рейтинга');
                console.error(e);
            } finally {
                setLoading(false);
            }
        };

        loadRating();
    }, [deviceId]);

    // Обработка выставления оценки
    const handleRate = async (rate) => {
        if (!user.isAuth) return;

        try {
            setLoading(true);
            setError(null);

            // Оптимистичное обновление UI
            setRatingState(rate);

            // Отправка на сервер (конвертация 0-100 → 0-5)
            await setRating(deviceId, rate / 20);

            // Обновление среднего рейтинга
            const avg = await fetchAverageRating(deviceId);
            setRatingState(avg * 20);
        } catch (e) {
            setError('Ошибка сохранения оценки');
            console.error(e);

            // Восстановление предыдущего значения
            const avg = await fetchAverageRating(deviceId);
            setRatingState(avg * 20);
        } finally {
            setLoading(false);
        }
    };

    if (loading) {
        return (
            <div className="d-flex justify-content-center">
                <div className="spinner-border text-primary" role="status">
                    <span className="visually-hidden">Загрузка...</span>
                </div>
            </div>
        );
    }

    return (
        <div className="mb-3">
            <Rating
                onClick={handleRate}
                initialValue={rating}
                size={30}
                allowFraction
                transition
                readonly={!user.isAuth || loading}
            />
            {error && (
                <div className="text-danger mt-2">
                    <small>{error}</small>
                </div>
            )}
        </div>
    );
};

export default DeviceRating;
