import { Context } from "../index";
import { Card, Row } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import React, { useContext } from "react";

const BrandBar = observer(() => {
    const { device } = useContext(Context);

    return (
        <Row className="d-flex">
            {device.brands.map(brand =>
                <Card
                    key={brand.id}
                    className="p-3 m-2"
                    style={{
                        cursor: 'pointer',
                        border: brand.id === device.selectedBrand?.id ? '2px solid #007bff' : '1px solid lightgray',
                        background: brand.id === device.selectedBrand?.id ? '#e9f5ff' : 'white',
                        borderRadius: '8px',         // скругление углов для формы как на картинке
                        minWidth: '100px',           // минимальная ширина карточки
                        textAlign: 'center',         // выравнивание текста по центру
                        userSelect: 'none',          // запрет выделения текста при клике
                        transition: 'all 0.3s ease'  // плавный переход при наведении/выборе
                    }}
                    onClick={() => device.setSelectedBrand(brand)}
                    onMouseEnter={e => {
                        if (brand.id !== device.selectedBrand?.id) {
                            e.currentTarget.style.border = '1.5px solid #007bff';
                            e.currentTarget.style.background = '#f0f8ff';
                        }
                    }}
                    onMouseLeave={e => {
                        if (brand.id !== device.selectedBrand?.id) {
                            e.currentTarget.style.border = '1px solid lightgray';
                            e.currentTarget.style.background = 'white';
                        }
                    }}
                >
                    {brand.name}
                </Card>
            )}
        </Row>
    );
});

export default BrandBar;
