import React, { useContext, useEffect, useState, useMemo } from 'react';
import { Col, Container, Row, Form, Button } from "react-bootstrap";
import TypeBar from '../components/TypeBar';
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchDevices } from "../http/deviceAPI";
import { observer } from "mobx-react-lite";
import Pages from "../components/Pages";
import MultiRangeSlider from "../components/MultiRangeSlider";

const Shop = observer(() => {
    const { device } = useContext(Context);
    const [search, setSearch] = useState("");
    const [isLoading, setIsLoading] = useState(true);
    const [priceBounds, setPriceBounds] = useState({ min: 0, max: 0 }); // Границы цен для текущих фильтров
    const [priceRange, setPriceRange] = useState({ min: 0, max: 0 }); // Применённый фильтр
    const [inputMin, setInputMin] = useState(0); // Вводимое значение мин
    const [inputMax, setInputMax] = useState(0); // Вводимое значение макс

    // Функция для получения границ цен
    const fetchPriceRange = async () => {
        try {
            const typeId = device.selectedType.id || null;
            const brandId = device.selectedBrand.id || null;

            const response = await fetch(`${process.env.REACT_APP_API_URL}api/device/price-range?${new URLSearchParams({
                typeId,
                brandId
            })}`);

            const data = await response.json();
            if (data) {
                const min = data.minPrice || 0;
                const max = data.maxPrice || 0;
                setPriceBounds({ min, max });
                setPriceRange({ min, max });
                setInputMin(min);
                setInputMax(max);
            }
        } catch (e) {
            console.error("Ошибка при получении диапазона цен:", e);
        }
    };

    // Загрузка типов, брендов и границ цен
    useEffect(() => {
        setIsLoading(true);
        Promise.all([
            fetchTypes(),
            fetchBrands(),
            fetchPriceRange() // Загружаем границы цен
        ])
            .then(([typesData, brandsData]) => {
                device.setTypes(typesData);
                device.setBrands(brandsData);
            })
            .finally(() => setIsLoading(false));
    }, [device]);

    // При изменении фильтров обновляем границы цен
    useEffect(() => {
        fetchPriceRange();
    }, [device.selectedType, device.selectedBrand]);

    // Загрузка устройств (пагинация)
    useEffect(() => {
        setIsLoading(true);
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 3)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .finally(() => setIsLoading(false));
    }, [device.page, device.selectedType, device.selectedBrand]);

    // Применение фильтра по цене
    const handleApplyPrice = () => {
        const min = Math.max(priceBounds.min, Math.min(Number(inputMin), Number(inputMax) - 1));
        const max = Math.min(priceBounds.max, Math.max(Number(inputMax), Number(inputMin) + 1));
        setPriceRange({ min, max });
    };

    // Сброс фильтра по цене
    const handleResetPrice = () => {
        setPriceRange({ min: priceBounds.min, max: priceBounds.max });
        setInputMin(priceBounds.min);
        setInputMax(priceBounds.max);
    };

    // Фильтрация устройств
    const filteredDevices = useMemo(() => {
        return device.devices.filter(dev =>
            dev.name.toLowerCase().includes(search.toLowerCase()) &&
            dev.price >= priceRange.min &&
            dev.price <= priceRange.max
        );
    }, [device.devices, search, priceRange]);

    return (
        <Container>
            <Row className="mt-2 align-items-center">
                <Col md={3}>
                    <Form.Control
                        type="text"
                        placeholder="🔍 Поиск по каталогу..."
                        value={search}
                        onChange={e => setSearch(e.target.value)}
                        style={{
                            marginBottom: 0,
                            borderRadius: 12,
                            fontSize: 17,
                            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                            padding: "10px 16px"
                        }}
                    />
                </Col>
                <Col md={9}>
                    <BrandBar />
                </Col>
            </Row>
            <Row className="mt-3">
                <Col md={3}>
                    <TypeBar />
                    {/* Фильтр по цене */}
                    <div className="mt-4 p-3" style={{
                        background: '#f8f9fa',
                        borderRadius: 12,
                        boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
                    }}>
                        <h6 className="mb-3">Цена</h6>
                        <MultiRangeSlider
                            min={priceBounds.min}
                            max={priceBounds.max}
                            minValue={inputMin}
                            maxValue={inputMax}
                            onChange={({ min, max }) => {
                                setInputMin(min);
                                setInputMax(max);
                            }}
                        />
                        <div className="d-flex justify-content-between mt-2">
                            <Form.Control
                                type="number"
                                value={inputMin}
                                onChange={e => setInputMin(e.target.value)}
                                style={{ width: '45%' }}
                                placeholder="Мин. цена"
                                min={priceBounds.min}
                                max={inputMax - 1}
                            />
                            <span className="mx-2">—</span>
                            <Form.Control
                                type="number"
                                value={inputMax}
                                onChange={e => setInputMax(e.target.value)}
                                style={{ width: '45%' }}
                                placeholder="Макс. цена"
                                min={inputMin + 1}
                                max={priceBounds.max}
                            />
                        </div>
                        <div className="text-muted mt-2" style={{ fontSize: '12px' }}>
                            Диапазон: {priceBounds.min.toLocaleString()} - {priceBounds.max.toLocaleString()} ₽
                        </div>
                        <Button
                            variant="primary"
                            className="mt-2 w-100"
                            onClick={handleApplyPrice}
                        >
                            Применить
                        </Button>
                        <Button
                            variant="link"
                            className="mt-1 p-0 w-100"
                            onClick={handleResetPrice}
                        >
                            Сбросить фильтр
                        </Button>
                    </div>
                </Col>
                <Col md={9}>
                    {isLoading ? (
                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">Загрузка...</span>
                            </div>
                        </div>
                    ) : (
                        <>
                            <DeviceList devices={filteredDevices} />
                            <Pages />
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
