import React, { useEffect, useState } from 'react';
import { Col, Container, Row, Image, Button, Card, Spinner, Alert } from "react-bootstrap";
import bigStar from '../assets/bigStar.png';
import { useParams } from 'react-router-dom';
import { fetchOneDevice } from "../http/deviceAPI";

const DevicePage = () => {
    const [device, setDevice] = useState({ info: [] });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        setLoading(true);
        setError(null);
        fetchOneDevice(id)
            .then(data => setDevice(data))
            .catch(e => setError(e.response?.data?.message || "Ошибка загрузки устройства"))
            .finally(() => setLoading(false));
    }, [id]);

    if (loading) {
        return (
            <Container className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
                <Spinner animation="border" variant="primary" />
            </Container>
        );
    }

    if (error) {
        return (
            <Container className="mt-5">
                <Alert variant="danger">
                    <h3>Ошибка!</h3>
                    <p>{error}</p>
                </Alert>
            </Container>
        );
    }

    return (
        <Container className='mt-3'>
            <Row>
                <Col md={4}>
                    <Image
                        width={300}
                        height={300}
                        src={
                            device.img
                                ? (device.img.startsWith('http') ? device.img : process.env.REACT_APP_API_URL + device.img)
                                : 'https://via.placeholder.com/300'
                        }
                        alt={device.name}
                        style={{ objectFit: 'cover' }}
                    />
                </Col>
                <Col md={4}>
                    <Row className="d-flex flex-column align-items-center">
                        <h2>{device.name}</h2>
                        <div
                            className="d-flex align-items-center justify-content-center"
                            style={{
                                background: `url(${bigStar}) no-repeat center center`,
                                width: 240,
                                height: 240,
                                backgroundSize: 'cover',
                                fontSize: 64
                            }}
                        >
                            {device.rating}
                        </div>
                    </Row>
                </Col>
                <Col md={4}>
                    <Card
                        className="d-flex flex-column align-items-center justify-content-around"
                        style={{ width: 300, height: 300, fontSize: 32, border: '5px solid lightgray' }}
                    >
                        <h3>От: {device.price} руб.</h3>
                        <Button variant="outline-dark">Добавить в корзину</Button>
                    </Card>
                </Col>
            </Row>
            <Row className="d-flex flex-column m-3">
                <h1>Характеристики</h1>
                {(device.info && device.info.length > 0 ? device.info : []).map((info, index) =>
                    <Row
                        key={info.id || index}
                        style={{
                            background: index % 2 === 0 ? 'lightgray' : 'transparent',
                            padding: 10
                        }}
                    >
                        {info.title}: {info.description}
                    </Row>
                )}
            </Row>
        </Container>
    );
};

export default DevicePage;
