import React, { useContext, useEffect, useState } from 'react';
import Modal from "react-bootstrap/Modal";
import { Button, Dropdown, Form, Row, Col } from "react-bootstrap";
import { Context } from "../../index";
import { createDevice, fetchBrands, fetchTypes } from "../../http/deviceAPI";
import { observer } from "mobx-react-lite";
import data from "bootstrap/js/src/dom/data";

const CreateDevice = observer(({ show, onHide }) => {
    const { device } = useContext(Context);
    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [file, setFile] = useState(null);
    const [info, setInfo] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data));
        fetchBrands().then(data => device.setBrands(data));
    }, [device]);

    const addInfo = () => {
        setInfo([...info, { title: '', description: '', number: Date.now() }]);
    };

    const removeInfo = number => {
        setInfo(info.filter(i => i.number !== number));
    };

    const changeInfo = (key, value, number) => {
        setInfo(info.map(i => i.number === number ? { ...i, [key]: value } : i));
    };

    const selectFile = e => {
        setFile(e.target.files[0]);
    };

    const resetForm = () => {
        setName('');
        setPrice('');
        setFile(null);
        setInfo([]);
        device.setSelectedType({});
        device.setSelectedBrand({});
        setError(null);
    };

    const addDevice = async () => {
        if (!name.trim() || !price || !file || !device.selectedType?.id || !device.selectedBrand?.id) {
            setError("Пожалуйста, заполните все обязательные поля.");
            return;
        }
        if (isNaN(price) || Number(price) <= 0) {
            setError("Цена должна быть положительным числом!");
            return;
        }
        setError(null);
        setLoading(true);

        try {
            const formData = new FormData();
            formData.append('name', name);
            formData.append('price', `${price}`);
            formData.append('img', file);
            formData.append('brandId', device.selectedBrand.id);
            formData.append('typeId', device.selectedType.id);
            formData.append('info', JSON.stringify(info));
            await createDevice(formData);
            resetForm();
            onHide();
        } catch (e) {
            setError(e.response?.data?.message || "Ошибка при создании устройства");
        } finally {
            setLoading(false);
        }
        device.setDevices([...device.devices, data]);
    };


    return (
        <Modal
            show={show}
            onHide={() => { resetForm(); onHide(); }}
            centered
            size="lg"
        >
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Добавить устройство
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle variant={device.selectedType?.id ? "success" : "secondary"}>
                            {device.selectedType?.name || "Выберите тип"}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.types.map(type =>
                                <Dropdown.Item
                                    onClick={() => device.setSelectedType(type)}
                                    key={type.id}
                                >
                                    {type.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Dropdown className="mt-2 mb-2">
                        <Dropdown.Toggle variant={device.selectedBrand?.id ? "success" : "secondary"}>
                            {device.selectedBrand?.name || "Выберите бренд"}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            {device.brands.map(brand =>
                                <Dropdown.Item
                                    onClick={() => device.setSelectedBrand(brand)}
                                    key={brand.id}
                                >
                                    {brand.name}
                                </Dropdown.Item>
                            )}
                        </Dropdown.Menu>
                    </Dropdown>
                    <Form.Control
                        value={name}
                        onChange={e => setName(e.target.value)}
                        className="mt-3"
                        placeholder="Введите название устройства"
                        disabled={loading}
                    />
                    <Form.Control
                        value={price}
                        onChange={e => setPrice(e.target.value.replace(/[^0-9.]/g, ''))}
                        className="mt-3"
                        placeholder="Введите стоимость устройства"
                        type="number"
                        min="0"
                        disabled={loading}
                    />
                    <Form.Control
                        className="mt-3"
                        type="file"
                        onChange={selectFile}
                        disabled={loading}
                    />
                    <hr />
                    <Button
                        variant="outline-dark"
                        onClick={addInfo}
                        disabled={loading}
                    >
                        Добавить новое свойство
                    </Button>
                    {info.map(i =>
                        <Row className="mt-4" key={i.number}>
                            <Col md={4}>
                                <Form.Control
                                    value={i.title}
                                    onChange={e => changeInfo('title', e.target.value, i.number)}
                                    placeholder="Введите название свойства"
                                    disabled={loading}
                                />
                            </Col>
                            <Col md={4}>
                                <Form.Control
                                    value={i.description}
                                    onChange={e => changeInfo('description', e.target.value, i.number)}
                                    placeholder="Введите описание свойства"
                                    disabled={loading}
                                />
                            </Col>
                            <Col md={4}>
                                <Button
                                    onClick={() => removeInfo(i.number)}
                                    variant="outline-danger"
                                    disabled={loading}
                                >
                                    Удалить
                                </Button>
                            </Col>
                        </Row>
                    )}
                    {error && <div className="text-danger mt-2">{error}</div>}
                </Form>
            </Modal.Body>
            <Modal.Footer>
                <Button
                    variant="outline-danger"
                    onClick={() => { resetForm(); onHide(); }}
                    disabled={loading}
                >
                    Закрыть
                </Button>
                <Button
                    variant="outline-success"
                    onClick={addDevice}
                    disabled={
                        loading ||
                        !name.trim() ||
                        !price ||
                        !file ||
                        !device.selectedType?.id ||
                        !device.selectedBrand?.id
                    }
                >
                    {loading ? "Добавление..." : "Добавить"}
                </Button>
            </Modal.Footer>
        </Modal>
    );
});

export default CreateDevice;
