import React, { useContext } from 'react';
import { Card, Col, Image, Button } from "react-bootstrap";
import { Rating } from 'react-simple-star-rating'; // Добавляем компонент рейтинга
import { useNavigate } from "react-router-dom";
import { DEVICE_ROUTE } from "../utils/constants";
import { Context } from "../index";

const DeviceItem = ({ device }) => {
    const navigate = useNavigate();
    const { basket } = useContext(Context);

    return (
        <Col md={3} className="mt-3">
            <Card
                style={{ width: 150, cursor: 'pointer' }}
                border="light"
                className="position-relative"
            >
                <Image
                    width={150}
                    height={150}
                    src={process.env.REACT_APP_API_URL + device.img}
                    onClick={() => navigate(`${DEVICE_ROUTE}/${device.id}`)}
                    style={{ objectFit: 'cover' }}
                />
                <div className='text-black-50 mt-1 d-flex justify-content-between align-items-center'>
                    <div>{device.brand?.name || "..."}</div>
                    <div className='d-flex align-items-center'>
                        {/* Компонент рейтинга */}
                        <Rating
                            initialValue={device.rating || 0}
                            readonly
                            size={15}
                            allowFraction
                            fillColor='#ffbc0b'
                            emptyColor='#ccc'
                        />
                        <span className="ms-1">{device.rating?.toFixed(1) || 0}</span>
                    </div>
                </div>
                <div style={{ fontWeight: 'bold' }}>{device.name}</div>
                <div className="d-flex justify-content-between align-items-center mt-2">
                    <div>{device.price} ₽</div>
                    <Button
                        variant="outline-primary"
                        size="sm"
                        onClick={(e) => {
                            e.stopPropagation();
                            basket.addToBasket(device);
                        }}
                    >
                        В корзину
                    </Button>
                </div>
            </Card>
        </Col>
    );
};

export default DeviceItem;
