import { makeAutoObservable } from "mobx";
import { jwtDecode } from "jwt-decode";

export default class UserStore {
    constructor(basketStore) {
        this._isAuth = false;
        this._user = {};
        this._role = "USER";
        this._basketStore = basketStore;
        makeAutoObservable(this);
    }

    setIsAuth(bool) {
        this._isAuth = bool;
    }

    setUser(user) {
        this._user = user;
    }

    setRole(role) {
        this._role = role;
    }

    login(token) {
        localStorage.setItem('token', token);
        const user = jwtDecode(token);
        this.setIsAuth(true);
        this.setUser(user);
        this._basketStore.setUserId(user.id); // ВАЖНО!
    }

    logout() {
        localStorage.removeItem('token');
        this.setIsAuth(false);
        this.setUser({});
        // Сбрасываем ID пользователя в корзине
        this._basketStore.setUserId(null);
    }

    get isAuth() {
        return this._isAuth;
    }

    get user() {
        return this._user;
    }

    get role() {
        return this._role;
    }
}
