import React, { useState, useEffect, useContext } from 'react';
import { Rating } from 'react-simple-star-rating';
import { setRating, fetchAverageRating } from '../http/ratingAPI';
import { Context } from '../index';



const DeviceRating = ({ deviceId }) => {
    const { user } = useContext(Context);
    const [rating, setRatingState] = useState(0);

    useEffect(() => {
        fetchAverageRating(deviceId).then(avg => setRatingState(avg * 20)); // переводим 0-5 в 0-100
    }, [deviceId]);

    const handleRate = (rate) => {
        setRatingState(rate);
        setRating(deviceId, rate / 20); // переводим обратно в 0-5
    };

    return (
        <Rating
            onClick={handleRate}
            initialValue={rating}
            size={30}
            allowFraction
            transition
            readonly={!user.isAuth}
        />
    );
};

export default DeviceRating;
