import React, { useContext, useEffect, useState, useMemo } from 'react';
import { Col, Container, Row, Form, Button } from "react-bootstrap";
import TypeBar from '../components/TypeBar';
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import { Context } from "../index";
import { fetchTypes, fetchBrands, fetchDevices } from "../http/deviceAPI";
import { observer } from "mobx-react-lite";
import Pages from "../components/Pages";
import MultiInputSlider from "react-multi-input-rangeslider";
import "react-multi-input-rangeslider/style/style.css";

const Shop = observer(() => {
    const { device } = useContext(Context);
    const [search, setSearch] = useState("");
    const [isLoading, setIsLoading] = useState(true);

    // Для фильтра по цене
    const prices = device.devices.map(dev => dev.price);
    const minPrice = prices.length ? Math.min(...prices) : 0;
    const maxPrice = prices.length ? Math.max(...prices) : 1000;
    const [priceRange, setPriceRange] = useState([minPrice, maxPrice]);

    useEffect(() => {
        setIsLoading(true);
        Promise.all([
            fetchTypes(),
            fetchBrands(),
            fetchDevices(null, null, 1, 100) // больше лимит, чтобы видеть весь диапазон цен
        ])
            .then(([typesData, brandsData, devicesData]) => {
                device.setTypes(typesData);
                device.setBrands(brandsData);
                device.setDevices(devicesData.rows);
                device.setTotalCount(devicesData.count);
            })
            .finally(() => setIsLoading(false));
    }, [device]);

    useEffect(() => {
        setIsLoading(true);
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 3)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .finally(() => setIsLoading(false));
    }, [device.page, device.selectedType, device.selectedBrand]);

    // Обновлять диапазон при смене ассортимента
    useEffect(() => {
        setPriceRange([minPrice, maxPrice]);
    }, [minPrice, maxPrice]);

    // Фильтрация устройств по поиску и цене
    const filteredDevices = useMemo(() => {
        return device.devices.filter(dev =>
            dev.name.toLowerCase().includes(search.toLowerCase()) &&
            dev.price >= priceRange[0] &&
            dev.price <= priceRange[1]
        );
    }, [device.devices, search, priceRange]);

    return (
        <Container>
            {/* Первая строка: поиск и бренды */}
            <Row className="mt-2 align-items-center">
                <Col md={3}>
                    <Form.Control
                        type="text"
                        placeholder="🔍 Поиск по каталогу..."
                        value={search}
                        onChange={e => setSearch(e.target.value)}
                        style={{
                            marginBottom: 0,
                            borderRadius: 12,
                            fontSize: 17,
                            boxShadow: "0 2px 8px rgba(0,0,0,0.04)",
                            padding: "10px 16px"
                        }}
                    />
                </Col>
                <Col md={9}>
                    <BrandBar />
                </Col>
            </Row>
            {/* Вторая строка: TypeBar, фильтр по цене и карточки */}
            <Row className="mt-3">
                <Col md={3}>
                    <TypeBar />
                    {/* Фильтр по цене */}
                    <div className="mt-4 p-3" style={{
                        background: '#f8f9fa',
                        borderRadius: 12,
                        boxShadow: '0 2px 6px rgba(0,0,0,0.05)'
                    }}>
                        <h6 className="mb-3">Цена</h6>
                        <MultiInputSlider
                            min={minPrice}
                            max={maxPrice}
                            minValue={priceRange[0]}
                            maxValue={priceRange[1]}
                            onChange={({ min, max }) => setPriceRange([min, max])}
                        />
                        <div className="d-flex justify-content-between mt-3">
                            <Form.Control
                                type="number"
                                value={priceRange[0]}
                                min={minPrice}
                                max={priceRange[1]}
                                onChange={e => setPriceRange([Number(e.target.value), priceRange[1]])}
                                style={{ width: '45%' }}
                            />
                            <span className="mx-2">—</span>
                            <Form.Control
                                type="number"
                                value={priceRange[1]}
                                min={priceRange[0]}
                                max={maxPrice}
                                onChange={e => setPriceRange([priceRange[0], Number(e.target.value)])}
                                style={{ width: '45%' }}
                            />
                        </div>
                        <Button
                            variant="link"
                            className="mt-2 p-0"
                            onClick={() => setPriceRange([minPrice, maxPrice])}
                        >
                            Сбросить фильтр
                        </Button>
                    </div>
                </Col>
                <Col md={9}>
                    {isLoading ? (
                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">Загрузка...</span>
                            </div>
                        </div>
                    ) : (
                        <>
                            <DeviceList devices={filteredDevices} />
                            <Pages />
                        </>
                    )}
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
